from functools import cache

class Solution:
    def closestCost(self, baseCosts: List[int], toppingCosts: List[int], target: int) -> int:
        # Double the toppingCosts list
        toppingCosts *= 2

        # Define a recursive function to find the sum of a subsequence of toppingCosts closest to x
        @cache
        def fn(i, x):
            """Return sum of subsequence of toppingCosts[i:] closest to x."""
            # Base cases
            if x < 0 or i == len(toppingCosts): 
                return 0

            # Recursive cases
            return min(fn(i+1, x), toppingCosts[i] + fn(i+1, x-toppingCosts[i]), key=lambda y: (abs(y-x), y))

        # Initialize a variable to store the closest cost
        ans = float('inf')

        # Iterate over the base costs
        for bc in baseCosts: 
            # Find the sum of toppingCosts closest to the target - base cost
            closest_sum = fn(0, target - bc)

            # Update the closest cost if necessary
            ans = min(ans, bc + closest_sum, key=lambda x: (abs(x-target), x))

        return ans

