    def validArrangement(self, pairs: List[List[int]]) -> List[List[int]]:
        graph = defaultdict(list)
        degree = defaultdict(int) # net out degree 
        for x, y in pairs: 
            graph[x].append(y)
            degree[x] += 1
            degree[y] -= 1

        for k in degree: 
            if degree[k] != 0: # Change 1 to 0 to consider connected components with no degrees
                x = k
                break 

        ans = []

        def fn(x): 
            """Return Eulerian path via dfs."""
            while graph[x]: fn(graph[x].pop()) 
            ans.append(x)

        fn(x) # You should call the function with 'x' instead of 's'
        ans.reverse()
        return [[ans[i], ans[i+1]] for i in range(len(ans)-1)]

