        for i in range(1, len(s)):  # Iterate through the remaining digits of s
            if int(s[i]) > k:
                return -1
            curr = 10 * curr + int(s[i])  # Update curr by multiplying it with 10 and adding the current digit
            if curr > k:
                ans += 1
                curr = int(s[i])  # Reset curr to current digit

