                if j != 0:
                    j=lps[j-1]
                else:
                    lps[i]=0
                    i+=1

            if s[i] == s[j]: # use "==" to compare equality, not "="
                lps[i]=j+1
                j+=1

        return s[:lps[-1]]

    def compareChar(self, s:str, i:int, j:int) -> bool:
        if i == len(s):
            return True
        elif s[i] != s[j]:
            return False
        else:
            return self.compareChar(s, i+1, j+1)

