        for i in range (m+1):
            dp[i][0]=i
        for i in range (n+1):
            dp[0][i]=i
        for i in range (1,m+1):
            for j in range (1,n+1):
                if word1[i-1]==word2[j-1]:
                    dp[i][j]=dp[i-1][j-1]
                else:
                    dp[i][j]=min(dp[i][j-1],dp[i-1][j])+1
        return dp[-1][-1]

class Solution:
    def kthLargestLevelSum(self, root: Optional[TreeNode], k: int) -> int:
        dq = collections.deque([root])
        a = []
        lvl = 1
        while dq:
            lvlsum = 0
            for i in range(len(dq)):
                n = dq.popleft()
                lvlsum += n.val
                if n.left:
                    dq.append(n.left)
                if n.right:
                    dq.append(n.right)

            a.append(self.calculateLevelSum(lvlsum)) 
            lvl += 1
        a.sort(reverse=True)
        return a[k-1] if len(a) >= k else -1

