        indmonth--;
        int monthpoint=costs[2]+helper(costs,days,indmonth,dp);
        return dp[days[ind]]=min({daypoint,weekpoint,monthpoint});
    }

    int mincostTickets(vector<int>& days, vector<int>& costs) {
        int n=days.size();
        vector<int>dp(366,-1);
        return helper(costs,days,n-1,dp);
    }
};

