// Fixed code
class Solution {
public:
    TreeNode* reverseOddLevels(TreeNode* root) {
        if(root == nullptr)
            return root;

        queue<TreeNode*> q;
        q.push(root);

        int level = 1;

        while(!q.empty()){
            int size = q.size();
            vector<int> temp;

            while(size--){
                TreeNode* node = q.front();
                q.pop();
                temp.push_back(node->val);

                if(node->left)
                    q.push(node->left);
                if(node->right)
                    q.push(node->right);

            }

            if(level % 2 == 1)
                reverse(temp.begin(), temp.end());

            int i = 0;
            size = temp.size();

            queue<TreeNode*> q2;

            while(i < size){
                TreeNode* node = q.front();
                q.pop();
                node->val = temp[i];
                i++;

                if(node->left)
                    q2.push(node->left);
                if(node->right)
                    q2.push(node->right);

            }

            swap(q, q2);
            level++;
        }

        return root;
    }
};

