            for(int j=0; j<m; j++){
                int p = i * m + j;
                int p1 = (i-1)*m + j;  // 1
                int p2 = (i+1)*m + j;  // 1
                int p3 = i*m + (j+1);  // 2
                int p4 = i*m + (j-1);  // 2

