class Solution {
public:
    using int2 = pair<int, int>;

    int shortestPathAllKeys(vector<string>& grid) {
        int n = grid.size();
        int m = grid[0].size();

        int2 start;
        // Scan the start and KeyMask
        int KeyMask = 0;
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < m; j++) {
                char c = grid[i][j];
                int2 pos = {i, j};
                if (c == '@')
                    start = pos;  // Store the position of the starting point
                if ('a' <= c && c <= 'z')
                    KeyMask |= (1 << (c - 'a'));  // Set the corresponding bit in KeyMask for each key found
            }
        }
        // Record the visiting status for every key status
        vector<vector<vector<bool>>> 
        visit(n, vector<vector<bool>>(m, vector<bool>(KeyMask+1, 0)));

        // [i, j, key state, distance]
        queue<tuple<int, int, int, int>> q;
        auto [i0, j0] = start;
        q.push({i0, j0, 0, 0});  // Push the starting point with initial key state and distance 0

        while (!q.empty()) {
            auto [i, j, keyState, distance] = q.front();  // Extract current position, key state, and distance from the front of the queue
            q.pop();
            vector<int2> adj = {{i + 1, j}, {i, j + 1}, {i - 1, j}, {i, j - 1}};
            for (auto [a, b] : adj) {
                if (a < 0 || a >= n || b < 0 || b >= m || grid[a][b] == '#')
                    continue;  // Skip if the next position is out of bounds or a wall ('#')
                char c = grid[a][b];
                // c is a key
                if ('a' <= c && c <= 'z') {
                    int keyIdx = c - 'a';  // Calculate the index of the key
                    int newState = keyState | (1 << keyIdx);  // Update the key state by setting the corresponding bit
                    if (visit[a][b][newState]) continue;  // Skip if this key state has been visited before at this position
                    visit[a][b][newState] = 1;  // Mark the current key state as visited at this position
                    if (newState == KeyMask) return distance + 1;  // If all keys are collected, return the distance
                    q.push({a, b, newState, distance + 1});  // Push the next position with updated key state and increased distance
                }
                // c is a lock
                else if ('A' <= c && c <= 'F') {
                    int lockIdx = c - 'A';  // Calculate the index of the lock
                    if ((keyState & (1 << lockIdx)) == 0) continue;  // Skip if the corresponding key for the lock is missing
                    if (visit[a][b][keyState]) continue;  // Skip if this key state has been visited before at this position
                    visit[a][b][keyState] = 1;  // Mark the current key state as visited at this position
                    q.push({a, b, keyState, distance + 1});  // Push the next position with the same key state and increased distance
                }
                // c is ',' or '@'
                else if (visit[a][b][keyState])
                    continue;  // Skip if this key state has been visited before at this position

                visit[a][b][keyState] = 1;  // Mark the current key state as visited at this position
                q.push({a, b, keyState, distance + 1});  // Push the next position with the same key state and increased distance
            }
        }

        return -1;  // If all keys cannot be collected, return -1
    }
};

