class Solution {
public:
    int maxSumDivThree(vector<int>& nums) {
        vector<int>dp(3, 0); // initialize dp array to all zeros
        for(auto a:nums){
            vector<int>temp(dp); // create a temp vector to store the current dp values
            for(int i=0; i<3; i++){
                int newSum = temp[i] + a; // compute the new sum
                int index = newSum % 3; // compute the index for the new sum
                dp[index] = max(dp[index], newSum); // update dp[index] with the maximum of dp[index] and newSum
            }
        }
        return dp[0]; // return the maximum sum that is divisible by 3
    }
};

