class Solution {
private:
int onecount(string s){
    int n = s.size();
    int cnt=0;
    for(int i=0;i<n;i++){
        if(s[i]=='1')cnt++;
    }
    return cnt;
}
int zerocount(string s){
    int n = s.size();
    int cnt=0;
    for(int i=0;i<n;i++){
        if(s[i]=='0')cnt++;
    }
    return cnt;
}
public:
    int minFlipsMonoIncr(string s) {
        int n = s.size();
        int mini = INT_MAX;
        vector<int> ans(n,0);
        for(int i=0;i<n;i++){
            ans[i]+= onecount(s.substr(0,i));
            ans[i]+= zerocount(s.substr(i+1));
            mini = min(mini,ans[i]);
        }
        return mini;
    }
};
