    int fillCups(vector<int>& am) 
    {
        priority_queue<int> pq;
        for(auto i :am)
        {
            pq.push(i);
        }
        int count=0;
        while(pq.top()!=0)
        {
            int a=pq.top();
            pq.pop();
            int b=pq.top();
            pq.pop();
            a--;
            b--;
            pq.push(a);
            pq.push(b);
            count++;
        }
        return count;
    }
};

