import java.util.Arrays;

class Solution {
    public int[] relativeSortArray(int[] arr1, int[] arr2) {
        int[] countArr = new int[1001]; // create a count array with size 1001 for all possible values in arr1

        // count the occurrence of each number in arr1
        for (int num : arr1) {
            countArr[num]++;
        }

        int index = 0; // index to keep track of the position in the ans array

        // first, add the numbers in arr2 to ans array in the order given in arr2
        for (int num : arr2) {
            while (countArr[num] > 0) {
                arr1[index++] = num;
                countArr[num]--;
            }
        }

        // then, add the remaining numbers in arr1 to ans array in ascending order
        for (int i = 0; i < countArr.length; i++) {
            while (countArr[i] > 0) {
                arr1[index++] = i;
                countArr[i]--;
            }
        }

        return arr1;
    }
}

