class Solution {
    public int passThePillow(int n, int time) {
        int num=(time/(n-1));
        if(n>time){
            return time+1;
        }
        if(num%2==1){
            return n-((time%(n-1)));
        }

        return (time%(n-1))+1;
    }
}

