class Solution {
    public String sortString(String s) {
        int[] arr = new int[26];
        for(int i=0;i<s.length();i++){
            int num = s.charAt(i) - 'a';
            arr[num]++; 
        }

        int len = s.length();
        int count=0;
        StringBuilder ans = new StringBuilder();

        while(count<len){
            for(int i=0;i<26;i++){
                if(arr[i]>0){
                    ans.append((char)(i + 'a'));
                    arr[i]--;
                    count++;
                }
            }
            for(int j=25;j>=0;j--){
                if(arr[j]>0){
                    ans.append((char)(j + 'a'));
                    arr[j]--;
                    count++;
                }
            }
        }
        return new String(ans);
    }
}
