class Solution {
    public void setZeroes(int[][] A) 
    {

       int row=A.length;
        int col=A[0].length;
        int x=0,y=0; <exp>Initialize x and y to 0 to represent if there are any zeros in the first row and column respectively.</exp>
        for(int i=0;i<col;i++){
            if(A[0][i]==0){
                x=1;
               break;}
        } 
        for(int i=0;i<row;i++){
            if(A[i][0]==0){
                y=1;
               break;}
        } 

        //update the first row and column if any zeros are found in the inner matrix
        for(int i=1;i<row;i++)
            for(int j=1;j<col;j++)
            {
                if(A[i][j]==0)
                {
                    A[i][0]=0;
                    A[0][j]=0;
                }
            }

       //set zeros in the inner matrix based on the first row and column
       for(int i=1;i<row;i++)
        {
           if(A[i][0]==0)
           {
               for(int j=1;j<col;j++)
                   A[i][j]=0;
           }
        }

        for(int j=1;j<col;j++)
        {
            if(A[0][j]==0)
            {
                for(int i=1;i<row;i++)
                    A[i][j]=0;
            }
        }

        //update the first row and column based on x and y
        if(x==1)
        for(int i=0;i<col;i++)
            A[0][i]=0;

        if(y==1)
        for(int i=0;i<row;i++)
            A[i][0]=0;

        System.gc();
        //return A;
    }
}

