            //System.out.println(top.node+" "+top.cost);
            curr_node=top.node;
            if(!vis.contains(top.node)){
            dfs(top.node);
            }
        }
        return ans;

    }
    public void dfs(int node){
        //System.out.println(node);
        if(ans[node]==-1){
            ans[node]=curr_node;
        }
        if(!vis.contains(node) && map.containsKey(node)){
            vis.add(node);

            for(int i : map.get(node)){
            dfs(i);

        }
        }

    }
}

