def fun(root, sm, mx):
    if root is None:
        return None
    left = fun(root.left, sm, mx)
    right = fun(root.right, sm, mx)
    sum_node = root.val
    if left is not None:
        sum_node += left
    if right is not None:
        sum_node += right
    mx[0] = max(mx[0], sum_node * (sm - sum_node))
    return sum_node

