        speed_heap = []
        speed_sum, perf = 0, 0
        for s, e in engineers:
            if len(speed_heap) > k - 1:
                speed_sum -= heapq.heappop(speed_heap)
            heapq.heappush(speed_heap, s)
            speed_sum += s
            perf = max(perf, speed_sum * e)
