class Solution:
    def maxPoints(self, points):
        if not points: return 0
        if len(points) <= 2: return len(points)

        def gcd(a,b):
            if b == 0:
                return a
            else:
                return gcd(b, a % b)

        points = [(x,y) for x, y in points] 
        points.sort()

        l = len(points)
        max_points = 0

        for i in range(l):
            dic = {'i': 1}
            same = 0

            for j in range(i + 1, l):
                dx, dy = points[j][0] - points[i][0], points[j][1] - points[i][1]
                if dx == 0 and dy == 0:
                    same += 1
                    continue

                g = gcd(dx, dy)
                if g != 0:
                    dx //= g
                    dy //= g

                if dx < 0:
                    dx, dy = - dx, - dy

                key = (dx, dy)

                if key in dic:
                    dic[key] += 1
                else:
                    dic[key] = 2

            max_points = max(max_points, max(dic.values()) + same)

        return max_points

