        for i in range(m-2, -1, -1):
            dp[i][n - 1] = dp[i + 1][n - 1] if grid[i][n - 1] == 1 else -1
        for j in range(n-2, -1, -1):
            dp[m - 1][j] = dp[m - 1][j + 1] if grid[m - 1][j] == 1 else -1

