class Solution:
    def canDistribute(self, nums: List[int], quantity: List[int]) -> bool:
        from collections import Counter
        from functools import lru_cache
        counter = Counter(nums)
        quantities = Counter(quantity)
        unique_items = sorted(counter.values(), reverse=True)
        unique_orders = sorted(quantities.elements(), reverse=True)
        orders_combinations = [0]*(2**len(unique_orders))
        for i in range(len(orders_combinations)):
            for j in range(len(unique_orders)):
                if (i>>j)&1:
                    orders_combinations[i] += unique_orders[j]
        unique_orders = sorted(list(quantities.elements()))
        @lru_cache(None)
        def dp(index, orders):
            if orders == 0: return True
            if index<0 or orders<0: return False
            return dp(index-1, orders) or dp(index-1, orders-unique_items[index])
        return dp(len(unique_items)-1, sum(unique_orders))

