class Solution:
    def decodeCiphertext(self, encoded_text: str, rows: int) -> str:
        num_chars = len(encoded_text.replace(' ', ''))

        # Calculate the number of columns used in encoding
        num_cols = (num_chars + rows - 1) // rows

        # Initialize the 2D array (matrix) that represents the encoded text
        encoded_matrix = [[' '] * num_cols for _ in range(rows)]

        # Populate the matrix with characters from the encoded text
        encoded_chars = iter(encoded_text)
        for i, row in enumerate(encoded_matrix):
            for j in range(i, num_cols):
                # Place character in the matrix if there are characters left in the encoded text
                try:
                    while row[j] == ' ':
                        row[j] = next(encoded_chars)
                except StopIteration:
                    break

        # Read characters from the matrix in the original order
        original_text = ''
        for i in range(num_cols):
            for j in range(rows):
                if i < j or i >= j + num_cols:
                    continue
                original_text += encoded_matrix[j][i]

        return original_text

