class Solution:
    def buildTree(self, grid, rs, re, cs, ce):
        if rs == re:
            return Node(grid[rs][cs]==1, True)
        midR, midC = (rs+re)//2, (cs+ce)//2
        topLeft = self.buildTree(grid, rs, midR, cs, midC)
        topRight = self.buildTree(grid, rs, midR, midC+1, ce)
        bottomLeft = self.buildTree(grid, midR+1, re, cs, midC)
        bottomRight = self.buildTree(grid, midR+1, re, midC+1, ce)
        if topLeft.isLeaf and topRight.isLeaf and bottomLeft.isLeaf and bottomRight.isLeaf and topLeft.val== topRight.val == bottomLeft.val == bottomRight.val:
            return Node(topLeft.val, True)
        else:
            return Node(False, False, topLeft, topRight,  bottomLeft, bottomRight)

    def construct(self, grid):
        if not grid: return None
        return self.buildTree(grid, 0, len(grid)-1, 0, len(grid[0])-1)

