class Solution:
    def minCost(self, grid: List[List[int]]) -> int:
        m, n = len(grid), len(grid[0])
        cost = [[float('inf')] * n for _ in range(m) ]
        moves = [(0,1,1), (0,-1,2), (1,0,3), (-1,0,4)]
        q = [(0,0,0)]
        cost[0][0] = 0

        def dfs(x, y):
            for dx, dy, z in moves:
                nx, ny, nz = x + dx, y + dy, z
                nc = cost[x][y] if grid[x][y] == nz else cost[x][y] + 1
                if 0 <= nx < m and 0 <= ny < n and nc < cost[nx][ny]:
