class Solution:
    def getAncestors(self, n: int, edges: List[List[int]]) -> List[List[int]]:
        from collections import defaultdict
        adj_graph = defaultdict(list)

        # build adjaceny graph
        for edge in edges:
            adj_graph[edge[1]].append(edge[0])

        ancestors = defaultdict(set)

        def dfs(node):
            if node not in ancestors:
                ancestors[node] = set(adj_graph[node])
                for ancestor in adj_graph[node]:
                    ancestors[node].update(dfs(ancestor))
            return ancestors[node]

        for node in range(n):
            dfs(node)

        return [sorted(list(ancestors[node])) for node in range(n)]

