class Solution:
    def ways(self, pizza: List[str], k: int) -> int:
        MOD = 10 ** 9 + 7
        n, m = len(pizza), len(pizza[0])
        apple = [[0 for _ in range(m + 1)] for _ in range(n + 1)]
        for i in range(n - 1, -1, -1):
            for j in range(m - 1, -1, -1):
                apple[i][j] = apple[i + 1][j] + apple[i][j + 1] - apple[i + 1][j + 1] + (pizza[i][j] == 'A')

        @lru_cache(None)
        def getWays(i, j, k):
            if k == 0: 
                return apple[i][j] > 0
            if apple[i][j] < k: 
                return 0
            ans = 0
            for x in range(i + 1, n):
                if apple[i][j] - apple[x][j] > 0: 
                    ans = (ans + getWays(x, j, k - 1)) % MOD
            for y in range(j + 1, m):
                if apple[i][j] - apple[i][y] > 0: 
                    ans = (ans + getWays(i, y, k - 1)) % MOD
            return ans
        return getWays(0, 0, k - 1)

