def fun(root, mx, mn, ans):
    if root is None:
        return mx - mn
    mx = max(mx, root.val)
    mn = min(mn, root.val)
    ans = max(ans, mx-mn)
    ans = max(ans, fun(root.left, mx, mn, ans))
    ans = max(ans, fun(root.right, mx, mn, ans))
    return ans

