class Solution:
    def minimumObstacles(self, grid: List[List[int]]) -> int:
        m, n = len(grid), len(grid[0])
        if m == 1 and n == 1: return 0 if grid[0][0] == 0 else 1
        if grid[0][0] == 1 or grid[m-1][n-1] == 1: return -1

        dirs = [(-1, 0), (0, -1), (1, 0), (0, 1)]
        q = [(0, 0, 0, {(0, 0)})]
        min_res = float('inf')

        while q:
            x, y, idx, obstacles = q.pop(0)

            for dx, dy in dirs:
                nx, ny = x + dx, y + dy

                if nx < 0 or nx >= m or ny < 0 or ny >= n: continue
                if (nx, ny) in obstacles: continue

                if nx == m-1 and ny == n-1:
                    min_res = min(min_res, idx + grid[m-1][n-1])

                else:
                    obstacles_n = set(obstacles)
                    obstacles_n.add((nx, ny))

                    if grid[nx][ny] == 1:
                        if idx + 1 < min_res:
                            q.append((nx, ny, idx + 1, obstacles_n))
                    else:
                        q.append((nx, ny, idx, obstacles_n))

        return -1 if min_res == float('inf') else min_res

