class Solution:
    def longestValidSubstring(self, word: str, forbidden: List[str]) -> int:
        n = len(word)
        dp = [0]*(n+1)
        for i in range(n):
            dp[i+1] = dp[i]
            for j in range(i+1):
                if word[j:i+1] not in forbidden:
                    dp[i+1] = max(dp[i+1], i-j+1)
        return dp[-1]

