            return 1.0
        neighbors = [[] for _ in range(n+1)]
        for i, j in edges:
            neighbors[i].append(j)
            neighbors[j].append(i)

        prob = [0]*(n + 1)
        prob[1] = 1.0

        q = collections.deque([1])
        seen = set()
        while q and t:
            for _ in range(len(q)):
                node = q.popleft()
                seen.add(node)
                total = sum(k not in seen for k in neighbors[node])
                for k in neighbors[node]:
                    if k not in seen:
                        prob[k] = prob[node] / total
                        q.append(k)
                if total:
                    prob[node] = 0         
            t -= 1        

        return prob[target] if t>=0 or not q else 0.0

