class Solution:
    def frogPosition(self, n: int, edges: List[List[int]], t: int, target: int) -> float:
        G = collections.defaultdict(list)
        for a, b in edges:
            G[a - 1].append(b - 1)
            G[b - 1].append(a - 1)
        visited = [False for _ in range(n)]
        probability = [0 for _ in range(n)]
        visited[0] = True
        probability[0] = 1
        queue = [(0, t)]  
        while queue:
            u, t = queue.pop(0)
            uv = sum(not visited[v] for v in G[u])
            for v in G[u]:
                if visited[v]:
                    continue
                visited[v] = True
                if t > 0:
                    probability[v] = probability[u] / uv
                    queue.append((v, t - 1))    
                if v == target - 1:
                    return probability[v] if sum(not visited[v] for v in G[v]) == 0 or t == 0 else 0
                if t == 0:
                    return 0
        return 0

