class Solution:
    def wordSubsets(self, words1: List[str], words2: List[str]) -> List[str]:
        # Iterate over words2 to find the maximum number of each character across all words in words2
        chars_in_words2 = [0]*26
        for word in words2:
            # Find the count of each character in the current word
            count_in_word = [0]*26
            for char in word:
                count_in_word[ord(char) - ord('a')] += 1
            # Update the maximum count of each character across all words in words2
            for i in range(26):
                chars_in_words2[i] = max(chars_in_words2[i], count_in_word[i])

        res = []
        for word in words1:
            # Track the count of each character in the current word from words1
            count_in_word = [0]*26
            for char in word:
                count_in_word[ord(char) - ord('a')] += 1

            # Check whether the count of each character in the current word is more than the maximum count across all words in words2
            if all(count_in_word[i] >= chars_in_words2[i] for i in range(26)):
                res.append(word)

        return res

