class Solution:
    def findSubstring(self, s: str, words: List[str]) -> List[int]:
        from collections import Counter
        result = []
        word_length = len(words[0])
        total_words = len(words)
        for i in range(len(s)-word_length*total_words + 1):
            words_seen = []
            for j in range(i, i+word_length*total_words, word_length):
                cur_word = s[j:j+word_length]
                words_seen.append(cur_word)
            if Counter(words) == Counter(words_seen):
                result.append(i)
        return result

