class Solution:
    def maxNiceDivisors(self, primeFactors: int) -> int:
        if primeFactors == 1: return 1
        MOD = 10**9 + 7
        divThree, divTwo = divmod(primeFactors, 3)

        if divTwo == 0:
            return self.calculateNiceDivisors(3, divThree, MOD)
        elif divTwo == 1:
            return (4 * self.calculateNiceDivisors(3, divThree - 1, MOD)) % MOD
        else:
            return (2 * self.calculateNiceDivisors(3, divThree, MOD)) % MOD 

    def calculateNiceDivisors(self, base: int, power: int, MOD: int) -> int:
        if power == 0: return 1
        if power % 2 == 0:
            half = self.calculateNiceDivisors(base, power // 2, MOD)
            return (half * half) % MOD
        else:
            return (base * self.calculateNiceDivisors(base, power - 1, MOD)) % MOD

