class Solution:
    def maxPower(self, stations: List[int], r: int, k: int) -> int:
        left = 1
        right = k + max(stations) + 1
        while left < right:
            mid = (left + right) // 2
            if self.canGeneratePower(stations, r, k, mid):
                left = mid + 1
            else:
                right = mid
        return left - 1

    def canGeneratePower(self, stations: List[int], r: int, k: int, target: int) -> bool:
        need = [max(0, target - sum(stations[max(0, i - r) : min(len(stations), i + r + 1)])) for i in range(len(stations))]
        total, i, q = sum(need), 0, deque()
        while i < len(stations) and total > k:
            while q and q[0][0] < i - r:
                total += q[0][1]
                q.popleft()
            while q and q[-1][1] <= need[i]:
                total += q[-1][1]
                q.pop()
            total -= need[i]
            q.append((i, need[i]))
            i += 1
        return total <= k

