class Solution:
    def maxValueAfterReverse(self, nums: List[int]) -> int:
        total, diff, min2, max2 = 0, 0, float('inf'), float('-inf')
        for i in range(1, len(nums)):
            total += abs(nums[i - 1] - nums[i])
            diff = max(diff, abs(nums[0] - nums[i]) - abs(nums[i] - nums[i - 1]))
            diff = max(diff, abs(nums[-1] - nums[i - 1]) - abs(nums[i] - nums[i - 1]))
            min2, max2 = min(min2, max(nums[i - 1], nums[i])), max(min2, min(nums[i - 1], nums[i]))
        return total + max(diff, 2 * (max2 - min2))

