from typing import Optional
from collections import deque

class Solution(object): 
    def findNode(self, root, value):
        if not root: 
            return None
        elif root.val == value: 
            return root
        left = self.findNode(root.left, value)
        if left: 
            return left
        return self.findNode(root.right, value)

    def getNodeDegree(self, node):
        degree = 0
        if node.left: 
            degree += 1
        if node.right: 
            degree += 1
        if node.parent: 
            degree += 1
        return degree

    def amountOfTime(self, root, start):
        parents = {root: None}
        self.findNode(root, start, parents)
        maxDegreeNode = root
        maxDegree = -1

        queue = deque([(root, 0)])
        while queue:
            node, level = queue.popleft()
            degree = self.getNodeDegree(node)
            if degree > maxDegree:
                maxDegree = degree
                maxDegreeNode = node
            if node.left: queue.append((node.left, level+1))
            if node.right: queue.append((node.right, level+1))

        return maxDegree

