class Solution:
    def levelOrderBottom(self, root: TreeNode) -> List[List[int]]:
        res = []

        if root is None:
            return res

        queue = [root]
        while queue:
            level = []
            for i in range(len(queue)):
                node = queue.pop(0)
                level.append(node.val)

                if node.left is not None:
                    queue.append(node.left)
                if node.right is not None:
                    queue.append(node.right)

            res.insert(0, level)

        return res

