    def findAllRecipes(self, recipes: List[str], ingredients: List[List[str]], supplies: List[str]) -> List[str]:
        size = len(recipes)
        recipeMap = {recipe: index for index, recipe in enumerate(recipes)}

        edges = [[] for _ in range(size)]
        inDegree = [0]*size

        for i in range(size):
            for ingredient in ingredients[i]:
                if ingredient not in recipeMap:
                    continue
                edges[recipeMap[ingredient]].append(i)
                inDegree[i] += 1

        queue = [i for i in range(size) if inDegree[i] == 0]

