        dic = collections.defaultdict(list)
        for i in range(len(s)):
            dic[self.find(i)].append(s[i])

        # Sort each group in reverse order.
        for k in dic.values():
            k.sort(reverse=True)

        # Re-build the string based on sorted parent-root groups
        res = []
        for i in range(len(s)):
            res.append(dic[self.find(i)].pop())

        return ''.join(res)

