class Solution:
    def smallestString(self, s: str) -> str:
        # Convert string to list to perform character replacements
        s_list = list(s)

        # Find first 'a' in string 
        for i in range(len(s_list)):
            if s_list[i] == 'a':
                break
            else:
                # Replace characters with previous alphabets
                s_list[i] = chr(ord(s_list[i])-1)
        # return smallest lexicographical string
        return ''.join(s_list)
