            for(int prev=1;prev>=0;prev--){
                if(prev){
                    curr[prev]=(s[idx]=='0')+dp[prev];
                }
                else{
                    curr[prev]=min((s[idx]=='1')+dp[0],(s[idx]=='0')+dp[1]);
                }
            }
            dp=curr;
        }
        return dp[0];
    }
};
