        int sum = 0 ; 
        for(auto it: s){
            int value = it - 'a' + 1;
            if(value>=10){
                sum+= value%10 + value/10; //since digit is two place only and we need all the digits(_ _)
            }else
                sum = sum + value; 
        }

        k--;//since I did the sum once;
        while(k-- && sum>=10){//furthur trasformations starts here
            sum = sumDigits(sum);
        } 
        return sum;
    }
};

