class Solution {
public:
    TreeNode* reverseOddLevels(TreeNode* root) {
        queue<TreeNode*> q;
        q.push(root);
        int x = 1,n,i,z;
        vector<TreeNode*> tmp;
        TreeNode* node;
        while(!q.empty()){
            tmp.clear();
            n = q.size();
            // cout<<n<<" ";
            while(n--){
                node = q.front();q.pop();
                // cout<<node->val<<" ";
                if(x==0){
                    tmp.push_back(node);
                }
                if(node->left)
                    q.push(node->left);
                if(node->right){
                    q.push(node->right);
                }
            }
            n = tmp.size();
            for(i = 0; i < n/2; i++){
                z = tmp[i]->val;
                tmp[i]->val = tmp[n-1-i]->val;
                tmp[n-1-i]->val = z;
            }
            x ^= 1;
            n = 0;
        }
        return root;
    }
};

