                if(x.first != 'r') // not coming from a red edge
                {
                    for(auto j:bluechilds[x.second])
                    {
                        visit.push({'b',j});
                    }
                    bluechilds[x.second].clear();
                } 
            }
            steps++;
        }
        return ans;
    }
