class Solution {
public:
    struct TreeNode {
      int val;
      TreeNode *left;
      TreeNode *right;
    };

    TreeNode* searchBST(TreeNode* root, int val) {
       if(root==NULL || root->val==val)
       {
          return root;
       }
       if(root->val>val){
          return searchBST(root->left,val);
       } else {
          return searchBST(root->right,val);
       }
    }
};

