class Solution {
    public int subtractProductAndSum(int n) {

        int multi=1, sum=0;
        while(n!=0){
            int rem=n%10; //take last digit of n
            sum+=rem;
            multi*=rem;
            n=n/10; // dropping last digit of n
        }
        // System.out.println(multi+" "+sum);
        return multi - sum;
    }
}

