class Solution {
    public String longestCommonPrefix(String[] strs) {
        Arrays.sort(strs);
        String start=strs[0];
         String end=strs[strs.length-1];
         int count=0;
         for(int i=0;i<start.length();i++){
             if(start.charAt(i)==end.charAt(i)){
            count++;
             }
             else{
                 break;
             }
         }

         return end.substring(0,count);
    }
}

