class Solution {
    public String toGoatLatin(String sentence) {
        sentence = sentence.toLowerCase();
        String[] words = sentence.split(" ");
        int i = 1;
        String ans = "";
        for(String word: words){
            char p1 = word.charAt(0);
            String k = "";
            if(p1 == 'a' || p1 == 'e' || p1 == 'i' || p1 == 'o' || p1 == 'u'){
                k += word + "ma";
            } else {
                k += word.substring(1) + p1 + "ma";
            }
            for(int m = 0; m < i; m++){
                k += 'a';
            }
            ans += k + " ";
            i++;
        }
        return ans.trim();
    }
}

